        [TestMethod]
        [TestCategory("TokenResponseTests")]
        public void GetResultTest()
        {
            TokenResponse response = new TokenResponse();
            response.IdTokenString =
                "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6Ik1uQ19WWmNBVGZNNXBPWWlKSE1iYTlnb0VLWSIsImtpZCI6Ik1uQ19WWmNBVGZNNXBPWWlKSE1iYTlnb0VLWSJ9..axS_-N3Z3b1GnZftxb6dKtMeooldoIQ_B7YrVO4CQI9xhHI1_Vl-dXfsFHBPRvIvXBEfBEehaaWq9B9P_CD5TpQXGycsYS08knHf_QpHIJ9WQbBIJ774divakx7kN6x7IxjoD1PrfRfo2QZsLLAz-1n-NHt7FwtkBQpKTDfgc6cVShy9isaJt5WoxfUM1eNo1HK_YjHj7Q5-n-XiZEbe-8m-7nqwBw86QDlLdk7dBhhCzVzXZb_5HCHI-23xZLYR34RoW7ljYEG4P8auEcML1haS4MN83VKRorMyljAIoA4YOgbfnvnlAlxRz_rtAAcjNqaUpIwzadGzd-QVbyoKPQ";
            response.AccessToken = "access-token";
            response.ExpiresIn = 3599;
            response.CorrelationId = "correlation-id";
            response.RefreshToken = "refresh-token";
            response.FamilyId = "1";
            response.Scope = "scope1 scope2";
            response.TokenType = "Bearer";

            AuthenticationResultEx resultEx = response.GetResult();
            Assert.IsNotNull(resultEx);
            Assert.AreEqual("access-token", resultEx.Result.AccessToken);
            Assert.AreEqual("scope1 scope2", resultEx.Result.ScopeSet.AsSingleString());
            Assert.AreEqual("Bearer", resultEx.Result.AccessTokenType);
        }
        
        private static TokenResponse CreateTokenResponse()
        {
            return new TokenResponse
            {
                AccessToken = "access_token",
                RefreshToken = "refresh_token",
                CorrelationId = Guid.NewGuid().ToString(),
                Scope = "my-resource",
                TokenType = "Bearer",
                ExpiresIn = 3899
            };
        }
    }
